    //gera hor√°rio 
    public void geraHorario(javax.swing.JTextField tf, int i){ 
        String local;
        String nome;
        
        if (JOptionPane.showConfirmDialog(null, "Voc√™ deseja cadastrar uma aula comum?", null,JOptionPane.YES_NO_OPTION) == 0){
            if(JOptionPane.showConfirmDialog(null, "Voc√™ deseja cadastrar uma aula?", "Cadastrar aula", JOptionPane.YES_NO_OPTION) == 0){
                nome = JOptionPane.showInputDialog( null, "Escreva o nome da disciplina que deseja cadastrar : ");
                String professor = JOptionPane.showInputDialog(null,"Escreva o nome do(a) professor(a)"
                        + "da disciplina que deseja cadastrar:");
                local = JOptionPane.showInputDialog(null, "Insira o local onde essa mat√©ria √© dada : ");
                Disciplina disciplina = new Disciplina(nome, professor);
                Aula aula = new Aula(disciplina, local, i);
                aulas.add(aula);
                tf.setText(aula.getNomeDisciplina());
            }else{
                if(JOptionPane.showConfirmDialog(null, "Voc√™ deseja cadastrar um compromisso semanal nesse hor√°rio?", "Cadastrar compromisso", JOptionPane.YES_NO_OPTION) == 0){
                    nome =  JOptionPane.showInputDialog(null, "Escreva o nome do compromisso que deseja cadastrar : ");
                    local = JOptionPane.showInputDialog(null, "Insira o local desse compromisso : ");
                    CompromissoSemanal compromisso = new CompromissoSemanal(i, nome, local);
                    compromissos.add(compromisso);
                    tf.setText(compromisso.getNome());
                }
            }
        }
    }
    
    
    //toString objeto do textfield
    public void printaCompromisso(javax.swing.JTextField tf) {
        if(verificaListCompromisso(tf) != -1){
            JOptionPane.showConfirmDialog(null, "Compromisso", getCompromisso(verificaListCompromisso(tf)).toString(),JOptionPane.PLAIN_MESSAGE);
        }else if(verificaListAula(tf) != -1){
            JOptionPane.showConfirmDialog(null, "Aula", getAula(verificaListAula(tf)).toString(),JOptionPane.PLAIN_MESSAGE);
        }
    }
   
    //getters
    public Aula getAula(int index) {
        return aulas.get(index);
    }
    
    public CompromissoSemanal getCompromisso(int index) {
        return compromissos.get(index);
    }
    
    
    //verificadores de compatibilidade objeto - textfield
    public int verificaListCompromisso(javax.swing.JTextField tf) {
        for (CompromissoSemanal compromisso : compromissos) {
            if (compromisso.getNome().equals(tf.getText())){
                return compromissos.indexOf(compromisso);
            }
        }
        return -1;
    }
    
    public int verificaListAula(javax.swing.JTextField tf){
        for (Aula aula : aulas) {
            if (aula.getNome().equals(tf.getText())){
                return aulas.indexOf(aula);
            }
        }  
        return -1;
    }
    
    
